    private void tb_simpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tb_simpanActionPerformed
        try {
    String sql = "INSERT INTO pembeli (id_pembeli, jumlah, nama_pembeli, no_telp, alamat, varian) VALUES (?, ?, ?, ?, ?, ?)";
    java.sql.Connection conn = (java.sql.Connection) projek.koneksi.koneksiDB();
    java.sql.PreparedStatement pst = conn.prepareStatement(sql);

    pst.setString(1, noPesanan.getText());
    pst.setString(2, jumlah.getText());
    pst.setString(3, Nama.getText());
    pst.setString(4, nomorHp.getText());
    pst.setString(5, Alamat.getText());
    pst.setString(6, Varian.getSelectedItem().toString());

    pst.executeUpdate();
    JOptionPane.showMessageDialog(null, "Berhasil disimpan");
    tampil_data();
} catch (Exception e) {
    JOptionPane.showMessageDialog(null, "Gagal di simpan: " + e.getMessage());
}
        
    }//GEN-LAST:event_tb_simpanActionPerformed

    private void tb_hapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tb_hapusActionPerformed
        // TODO add your handling code here:
        try {
    java.sql.Connection conn = (java.sql.Connection) projek.koneksi.koneksiDB();
    String sql = "DELETE FROM pembeli WHERE nama_pembeli = ?";
    java.sql.PreparedStatement pst = conn.prepareStatement(sql);
    pst.setString(1, Nama.getText()); // Menggunakan nama pembeli untuk menghapus

    int result = pst.executeUpdate();
    if (result > 0) {
        JOptionPane.showMessageDialog(null, "Data berhasil dihapus");
        tampil_data();
    } else {
        JOptionPane.showMessageDialog(null, "Data dengan nama " + Nama.getText() + " tidak ditemukan");
    }
} catch (Exception e) {
    JOptionPane.showMessageDialog(null, "Proses Penghapusan Gagal: " + e.getMessage());
    System.out.println(e.getMessage());
}

    }//GEN-LAST:event_tb_hapusActionPerformed

    private void CariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CariActionPerformed
        // TODO add your handling code here:
       try {
        java.sql.Connection conn = (java.sql.Connection) projek.koneksi.koneksiDB();
        String sql = "Select * from pembeli where id_pembeli=? or nama_pembeli=?";
        java.sql.PreparedStatement pst = conn.prepareStatement(sql);
        pst.setString(1, isiCari.getText());
        pst.setString(2, isiCari.getText());
        ResultSet rs = pst.executeQuery();

        if (rs.next()){
            noPesanan.setText(rs.getString("id_pembeli"));
            jumlah.setText(rs.getString("jumlah"));
            Nama.setText(rs.getString("nama_pembeli"));
            nomorHp.setText(rs.getString("no_telp"));
            Alamat.setText(rs.getString("alamat"));
            Varian.setSelectedItem(rs.getString("varian"));
            JOptionPane.showMessageDialog(null, "Data " + isiCari.getText() + " ditemukan ");
        }
    } catch (Exception e) {
        JOptionPane.showMessageDialog(null, "Pencarian data gagal: " + e.getMessage());
    }
    isiCari.requestFocus();
    }//GEN-LAST:event_CariActionPerformed

