    private void tb_editActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tb_editActionPerformed
        // TODO add your handling code here:                                    
    try {
        java.sql.Connection conn = (java.sql.Connection) projek.koneksi.koneksiDB();
        String sql = "UPDATE pembeli SET jumlah=?, no_telp=?, alamat=?, varian=? WHERE nama_pembeli=?";
        java.sql.PreparedStatement pst = conn.prepareStatement(sql);

        //pst.setString(1, noPesanan.getText());
        pst.setString(1, jumlah.getText());
        pst.setString(2, nomorHp.getText());
        pst.setString(3, Alamat.getText());
        pst.setString(4, Varian.getSelectedItem().toString());
        pst.setString(5, Nama.getText()); // Berdasarkan nama_pembeli

        int result = pst.executeUpdate();
        if (result > 0) {
            JOptionPane.showMessageDialog(null, "Data berhasil diperbarui");
            tampil_data();
        } else {
            JOptionPane.showMessageDialog(null, "Data dengan nama " + Nama.getText() + " tidak ditemukan");
        }
    } catch (Exception e) {
        JOptionPane.showMessageDialog(null, "Proses pengeditan gagal: " + e.getMessage());
        System.out.println(e.getMessage());
    }

    }//GEN-LAST:event_tb_editActionPerformed

