   public void tampil_data() {
    DefaultTableModel model = (DefaultTableModel) tabel.getModel();
    model.setRowCount(0); // Menghapus semua baris di model tabel sebelum menambahkan data baru

    // Define the new column headers
    String[] columnHeaders = {"no_Pesanan", "jumlah", "nama", "no_telp", "alamat", "varian"};

    // Set the new column headers to the table model
    model.setColumnIdentifiers(columnHeaders);

    try {
        java.sql.Connection conn = (java.sql.Connection) projek.koneksi.koneksiDB();
        String sql = "SELECT * FROM pembeli"; // Perhatikan perbaikan penulisan SQL
        java.sql.PreparedStatement pst = conn.prepareStatement(sql);
        ResultSet rs = pst.executeQuery();

        while (rs.next()) {
            model.addRow(new Object[]{
                rs.getString("id_pembeli"),
                rs.getString("jumlah"), // Sesuaikan dengan nama kolom pada tabel database
                rs.getString("nama_pembeli"),
                rs.getString("no_telp"),
                rs.getString("alamat"),
                rs.getString("varian")
            });
        }
    } catch (Exception e) {
        JOptionPane.showMessageDialog(null, "Gagal mengambil data: " + e.getMessage());
    }
}






